mapper.select:
select *
from t_record
where user_id = #{userId}
  and upload_date = #{date,jdbcType=DATE}

SELECT t_user.username, t_record.*
FROM t_user
         LEFT JOIN t_record
                   ON t_user.id = t_record.user_id
WHERE t_user.class_number = #{classNumber}
  AND t_record.upload_date = #{date,jdbcType=DATE}
ORDER BY t_record.user_id;
